import logo from './logo.svg';
import './App.css';
import Header from './MyComponents/Header';
import {Todos} from './MyComponents/Todos';
import {Footer} from './MyComponents/Footer';
import React, { useState } from 'react';
import AddTodo from './MyComponents/AddTodo';

function App() {
  const onDelete = (todo)=>{
    console.log("Iam ondelete of todo", todo);
    // deleting this way does not work
    // let index  = todos.indexOf(todo);
    // todos.splice(index, 1);
    setTodos(todos.filter((e)=>{
      return e!== todo;
    }))
  }
  const addTodo =(title,desc)=>{
    console.log("Ia m adding todo,", title,desc)
    let sno = todos[todos.length-1].sno + 1
    const myTodo ={
      sno: sno,
      title: title,
      desc: desc,
    }
  }
  const[todos, setTodos] = useState([

    {
      sno: 1,
      title: "Go to the market",
      desc: "you need to go to the market"
    },
    {
      sno: 2,
      title: "Go to the mall",
      desc: "you need to go to the market to do job2"
    },
    {
      sno: 3,
      title: "Go to the ghat",
      desc: "you need to go to the market to do job3"
    },
  ]);
  return (
    <>
    <Header title = "MyTodosList" />
    <AddTodo addTodo = {addTodo}/>
    <Todos todos = {todos} onDelete = {onDelete}/>
    <Footer/>

    </>
  );
}

export default App;
