import React, { useState } from 'react'

export const AddTodo = (props) => {
    const[title, setTitle] = useState("");
    const[desc, setDesc] = useState("");
    
     const submit =(e)=>{
         e.preventDefault();
         if(!title || !desc){
             alert("Title or desc cannot be blank")
         }

    }
    return (
        <div className='container' my-3>
            <h3>Add a todo</h3>
            <form onSubmit={submit}>
                <div class="mb-3">
                    <label for="tilte" class="form-label">Todo title</label>
                    <input type="text" value={title} onChange ={(e)=>{setTitle(e.target.value)}} class="form-control" id="title" aria-describedby="emailHelp"/>
                </div>
                <div class="mb-3">
                    <label for="desc" class="form-label">Todo description</label>
                    <input type="desc" value={desc} onChange ={(e)=>{setDesc(e.target.value)}} class="form-control" id="desc"/>
                </div>
                <button type="submit" class="btn btn-primary">Add Todo</button>
            </form>
        </div>
    )
}

export default AddTodo
